// –í—Ö–æ–¥: items –æ—Ç IMAP Email Trigger
// –í—ã—Ö–æ–¥: —Ç–æ–ª—å–∫–æ –Ω—É–∂–Ω—ã–µ –ø–æ–ª—è: from, date, subject, textPlain

const out = [];

for (const item of items) {
  const data = item.json;

  // –û—Ç–ø—Ä–∞–≤–∏—Ç–µ–ª—å
  const from = data.from || "";

  // –î–∞—Ç–∞
  const date = data.date || "";

  // –¢–µ–º–∞
  const subject = data.subject || "";

  // –¢–µ–ª–æ –ø–∏—Å—å–º–∞ (plain text)
  const textPlain = data.textPlain || "";

  out.push({
    json: {
      from,
      date,
      subject,
      textPlain
    }
  });
}

return [
  {
    json: {
      messageId: $json.messageId || $json.uid || "",
      from: $json.from,
      date: $json.date,
      subject: $json.subject,
      textPlain: $json.textPlain
    }
  }
];

let text = $json.text;

// –£–¥–∞–ª—è–µ–º –æ—Ñ–æ—Ä–º–ª–µ–Ω–∏–µ ```json ... ```
text = text
  .replace(/```json/gi, '')
  .replace(/```/g, '')
  .trim();

return JSON.parse(text);

// –†–µ–∑—É–ª—å—Ç–∞—Ç LLM (–æ—á–∏—â–µ–Ω–Ω—ã–π JSON)
let llm = $json || {};

// –û—Ä–∏–≥–∏–Ω–∞–ª—å–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ –ø–∏—Å—å–º–∞/—Å–æ–æ–±—â–µ–Ω–∏—è
let original = $input.item.json || {};

return {
  // –∏–¥–µ–Ω—Ç–∏—Ñ–∏–∫–∞—Ç–æ—Ä—ã
  messageId: original.messageId || "",
  chatId: original.chatId || "",        // –Ω—É–∂–Ω–æ –¥–ª—è Telegram
  from_email: original.from || "",      // –Ω—É–∂–Ω–æ –¥–ª—è Email
  from_id: original.from_id || "",
  from_language: original.from_language || "",

  // –∏—Å—Ö–æ–¥–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ
  subject: original.subject || "",
  text_plain: original.textPlain || "",
  date: original.date || "",
  source: original.source || "telegram",

  // –∫–ª–∞—Å—Å–∏—Ñ–∏–∫–∞—Ü–∏—è
  department: llm.department || "",
  category: llm.category || "",
  priority: llm.priority || "",
  problem_type: llm.problemType || "",
  reasoning: llm.reasoning || "",
  language: llm.language || "",

  // –æ—Ç–≤–µ—Ç—ã
  short_reply: llm.shortReply || "",
  full_reply: llm.fullReply || ""
};

// –ë–µ—Ä—ë–º –¥–∞–Ω–Ω—ã–µ –∏–∑ Code in JavaScript2
let data = $json || {};

// –í–æ–∑–≤—Ä–∞—â–∞–µ–º —Ç–æ–ª—å–∫–æ —Ç–µ –ø–æ–ª—è, –∫–æ—Ç–æ—Ä—ã–µ —Ä–µ–∞–ª—å–Ω–æ –µ—Å—Ç—å –≤ —Ç–∞–±–ª–∏—Ü–µ client_requests
return {
  from_email: data.from_email || "",
  subject: data.subject || "",
  text_plain: data.text_plain || "",
  date: data.date || "",
  source: data.source || "telegram",   // –∏–ª–∏ "email" –≤ –∑–∞–≤–∏—Å–∏–º–æ—Å—Ç–∏ –æ—Ç —Ñ–ª–æ—É
  department: data.department || "",
  category: data.category || "",
  priority: data.priority || "",
  problem_type: data.problem_type || "",
  reasoning: data.reasoning || "",
  language: data.language || "",
  short_reply: data.short_reply || "",
  full_reply: data.full_reply || ""
};
